function openConnectPersonClick(n,t){$(".invitation-text>.user-name").text(n);$("#ChosenConnectUserId").val(t);$.ajax({type:"GET",url:"/Relationship/GetCurrentCompanyJobsNotConnectedWithUser",data:{userId:t},success:function(n){var t=$(".job-dropdown>select");$(t).find("option").remove();jQuery(n).each(function(n,i){$(t).append("<option value='"+i.id+"'>"+i.title+"<\/option>")})},error:function(n,t,i){console.log("Relationship.GetCurrentCompanyJobsThatNotConnectedWithUser: some problem happened. ErrorMessage: "+t+" ErrorThrown:"+i)}})}